import axios from 'axios';
import Cookies from 'js-cookie';
import { USER_SIGNIN_REQUEST, USER_SIGNIN_SUCCESS, USER_SIGNIN_FAIL } from '../constants/userConstants';

const signin = (email,password,name) => async (dispatch) =>{
    dispatch({ type: USER_SIGNIN_REQUEST, payload: {email,password,name} });
    try {
        const { data } = await axios.post("/api/v1/users/signin",{ email,password,name });
        dispatch({ type: USER_SIGNIN_SUCCESS, payload: data });
        Cookies.set('userInfo',JSON.stringify(data));
    } catch (error) {
        dispatch({ type: USER_SIGNIN_FAIL, payload: error.message });
    }
}

export { signin }
