import React, { useEffect } from 'react';
import { addToCart } from '../../actions/cartActions';
import { useDispatch, useSelector } from 'react-redux';


function CartScreen(props) {

	const cart = useSelector( state => state.cart );
	const { cartItems } = cart;
  	const productId = props.match.params.id;
  	const dispatch = useDispatch();
	useEffect(()=>{
		if(productId){
		dispatch(addToCart(productId));
		}
	}, []);
	return (
		<div class="wrap cf">
			<h1 class="projTitle">Shopping Cart</h1>
			<div class="heading cf">
				<h1>My Cart</h1>
				<a href="#" class="continue">Continue Shopping</a>
			</div>
			<div class="cart">
				<ul class="cartWrap">
					{
						cartItems.length === 0 ?
						<div>Cart is empty</div>
						:
						cartItems.map( item =>{
							<li className="items odd">
								<div className="infoWrap">
									<li className="items even">
										<div className="infoWrap">
											<div className="cartSection">
												<img src={item.image} alt="" className="itemImg" />
												<p className="itemNumber">{ item._id }</p>
												<h3>{item.name}</h3>
											</div>
											<div className="prodTotal cartSection">
												<p>${item.price}</p>
											</div>
											<div className="cartSection removeWrap">
												<a href="#" className="remove">x</a>
											</div>
										</div>
									</li>
								</div>
							</li>
						})
					}
				</ul>
				<div class="subtotal cf">
					<ul>
						<li class="totalRow final">
							<span class="label">Total</span>
							<span class="value">${ cartItems.reduce( (a, c) => a + c.price,0 ),0 }</span>
						</li>
					</ul>
				</div>
			</div>
		</div>
  	)
}

export default CartScreen;
