import React, { useState, useEffect } from 'react';
import LeftAside from '../../components/LeftAside/LeftAside';
import ContainerProducts from '../../components/ContainerProducts/ContainerProducts';
import { useSelector, useDispatch } from 'react-redux';
import { listProducts, productsCategories } from '../../actions/productActions';

function HomeScreen(props){
	const category = "category" in props.match.params ? props.match.params.category : {};
	const productList = useSelector(state => state.productList);
	const productCategories = useSelector(state => state.categories);
	const { products, loading, error } = productList;
	const { categories } = productCategories;
	const dispatch = useDispatch();

	useEffect(()=>{
		dispatch(listProducts(category));
		dispatch(productsCategories());
	}, []);
	return (
		loading ? <div>Loading...</div> :
		error   ? <div>{error}</div>:
		<div className="homeScreen">
			<LeftAside categories={categories} props={props}/>
			<ContainerProducts products={products}/>
		</div>
	)
}

export default HomeScreen;
